INSERT INTO `pre_article` VALUES ('7', '东田度假酒店', '阿萨德撒打算的', null, '1485273600', '东田度假', '<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201605271046366085662.jpg\" title=\"DSC_0191.jpg\" width=\"700\" height=\"464\" border=\"0\" style=\"width:700px;height:464px;\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	酒店稻香居餐厅共有十个风格各异的包厢，另设精致雅座、大堂玻璃花园餐厅。提供别具风味的农家养生菜系，四季时蔬多为园区种植，无公害、无污染，当时采摘，确保新鲜，独创菜式更是美味可口，营养丰富，是你乐享美食的绝佳场所。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201606030939454281508.jpg\" title=\"包厢_副本.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201606030939459921831.jpg\" title=\"pic (25)_副本.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201606030939482933147.jpg\" title=\"pic (26)_副本.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201606030939488243451.jpg\" title=\"pic (27)_副本.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	酒店周边水域设有垂钓区及特色小木屋式的棋牌室，环境舒适、空气清新，处处散发着田园气息，您可以享受静怡的感觉，拥有更多的私人空间，度过舒怡、放松的假期。<img src=\"http://officialsite.juyingzhiye.com/2016/201606030939496473922.jpg\" title=\"pic (28)_副本.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	35间客房宽敞明亮，装修考究，设备先进，是您修养身息的绝佳场所，可以从客房欣赏到农夫乐园里的生态农业景色。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201605271046348234641.JPG\" title=\"pic (31).JPG\" width=\"700\" height=\"464\" border=\"0\" style=\"width:700px;height:464px;\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201605271046382566605.JPG\" title=\"pic (30).JPG\" width=\"700\" height=\"452\" border=\"0\" style=\"width:700px;height:452px;\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<img src=\"http://officialsite.juyingzhiye.com/2016/201605271046404347851.JPG\" title=\"玻璃大厅.JPG\" width=\"700\" height=\"526\" border=\"0\" style=\"width:700px;height:526px;\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	多功能会议室可容纳200人左右，设施齐全，配有灯光、音响、双投影、KTV等设备，为会议室各类活动提供了良好的环境。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	餐饮部有20多个包厢，其中豪华大包厢一个。玻璃大厅+雅座可容纳300人左右同时就餐。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<strong>酒店客房及餐饮预订电话：0571-88787888/0571-88781777</strong>\r\n</p>', '1');
