INSERT INTO `pre_article` VALUES ('46', '关于我们', '关于我们', 'static/images/201610281114470512541.jpg!640320', '1484150400', '关于我们', '<div class=\"intro_main_tit\" style=\"color:#333333;font-family:微软雅黑;\">\r\n	<h2 style=\"font-size:24px;font-weight:400;\">\r\n		关于我们\r\n	</h2>\r\n</div>\r\n<div class=\"intro_main_tips\" style=\"padding:25px 40px 40px;color:#333333;font-family:微软雅黑;\">\r\n	<div class=\"intro_main_essay_text\" style=\"color:#666666;\">\r\n		<p>\r\n			<span style=\"line-height:1.5em;font-family:arial, helvetica, sans-serif;font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; “农夫乐园是一个以文化创意为魂，以绿色环保、亲子动漫为主题，颠覆传统农业旅游形态的国家AAA级生态亲子主题旅游景区。2010年至2013年连续四届杭州西博会项目之一；2011年-2015年连续5届国际动漫节分会场，被评为中国国际动漫节最佳分会场；连续举办两届“冰雕节”；2011年开始至2015年秋季每年举办“乡村狂欢节”。<br />\r\n</span>\r\n		</p>\r\n		<p>\r\n			<span style=\"font-family:arial, helvetica, sans-serif;font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;整个景区分六大主题公园，水上主题公园、动物主题公园、爱情主题公园、休闲美食主题公园、儿童游乐主题公园、休闲度假主题公园，以创意卡通表演为核心，以休闲创意为核心，以亲子旅游为特色，多维度深层次挖掘现代农业创意旅游，打造杭州首家创意卡通农业休闲相结合的亲子旅游主题公园。</span>\r\n		</p>\r\n		<p>\r\n			<span style=\"font-family:arial, helvetica, sans-serif;font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2016年农夫乐园横向拓展项目，引进高端马术俱乐部“凯得国际马术中心”及“野狼户外”专业CS拓展团队，同时2016年动漫节期间将与《少年师爷》动画强强联合，突破传统旅游跨界整合资源，多层次多维度挖掘亲子旅游产品的同时加强成人市场影响力。</span>\r\n		</p>\r\n	</div>\r\n</div>', '4');
INSERT INTO `pre_article` VALUES ('47', '交通线路', '交通线路', 'static/images/201609251534303848285.jpg!640320', '1485100800', '交通线路', '<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<iframe class=\"ueditor_baidumap\" src=\"http://www.ajsxdsl.com/Static/ueditor/dialogs/map/show.html#center=120.068085,30.392873&amp;zoom=16&amp;width=696&amp;height=340&amp;markers=120.064277,30.390436&amp;markerStyles=l,A\" frameborder=\"0\" width=\"698\" height=\"342\">\r\n	</iframe>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>线路一：</strong>上塘高架→南庄兜枢纽（转入绕城高速西线）→勾庄出口→安吉方向（转入新104国道）→东西大道塘栖方向→农夫乐园</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>线路二：</strong>莫干山路→老104国道→东西大道塘栖方向→农夫乐园</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>线路三：</strong>古墩路-东西大道塘栖方向-农夫乐园</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>公交线路：</strong>乘313.348至良渚公交站下→东西大道塘栖方向→农夫乐园</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>高速线路：</strong>① 转入杭州绕城北线→勾庄出口→安吉、湖州方向→转入东西大道塘栖方向（加油站右转）→农夫乐园</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;\"><strong>高速线路：</strong>② 杭宁高速→良渚出口→东西大道塘栖方向→农夫乐园</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>', '5');
